import { Suspense, useState, useEffect, useRef, useCallback, useMemo } from 'react';
import { Command as CommandIcon, X as CloseIcon } from 'lucide-react';
import { Routes, Route, Navigate, useLocation } from 'react-router-dom';
import { AuthProvider } from './contexts/AuthProvider';
import { useAuth } from './hooks/useAuth';
import { useToast } from './contexts/ToastContext';
import { LoginForm } from './components/auth/LoginForm';
import { RegisterForm } from './components/auth/RegisterForm';
import { Header } from './components/layout/Header';
import { LogoLoader } from './components/common/LogoLoader';
import { OfflineIndicator } from './components/common/OfflineIndicator';
import { SyncErrorHandler } from './components/common/SyncErrorHandler';
import { SyncQueueModal } from './components/common/SyncQueueModal';
import { SkipLinks } from './components/common/SkipLinks';
import { CommandNavigation } from './components/navigation/CommandNavigation';
import { ThemeSyncProvider, useThemeSync, resolveThemeKeyFromRoute } from './contexts/ThemeSyncContext';
import { AdaptiveAtmosphereProvider } from './contexts/AdaptiveAtmosphereContext';
import { usePrefersReducedMotion } from './hooks/usePrefersReducedMotion';
import {
  LazyDashboard,
  LazyDocumentsPage,
  LazyCRMPage,
  LazyAdminPage,
} from './lib/lazyLoader';

type AuthView = 'login' | 'register';

// Protected route wrapper for admin pages
const AdminRoute = ({ children, isAdmin }: { children: React.ReactNode; isAdmin: boolean }) => {
  if (!isAdmin) {
    return <Navigate to="/crm" replace />;
  }
  return children;
};

const RouteSuspenseFallback = ({ onStart, onStop }: { onStart: () => void; onStop: () => void }) => {
  useEffect(() => {
    onStart();
    return () => {
      onStop();
    };
  }, [onStart, onStop]);

  return <LogoLoader fullScreen size="lg" showText label="Loading..." />;
};

const AppContent = () => {
  const { user, isLoading, refreshUser, isAdmin, isMarketing } = useAuth();
  const { showToast } = useToast();
  const [authView, setAuthView] = useState<AuthView>('login');
  const [hasShownOfflineToast, setHasShownOfflineToast] = useState(false);
  const hasShownOfflineToastRef = useRef(false);
  const location = useLocation();
  const [isCommandVisible, setCommandVisible] = useState(true);
  const isInitializedRef = useRef(false);
  const { setTheme, clearPreview, theme } = useThemeSync();
  const commandSectionRef = useRef<HTMLDivElement | null>(null);
  const prefersReducedMotion = usePrefersReducedMotion();
  const [isRouteLoading, setIsRouteLoading] = useState(false);
  // Memoize theme properties to prevent unnecessary re-renders
  const { accentSoft, accentGlow, onAccent } = useMemo(() => ({
    accentSoft: theme.accentSoft,
    accentGlow: theme.accentGlow,
    onAccent: theme.onAccent,
  }), [theme.accentSoft, theme.accentGlow, theme.onAccent]);
  const shouldReduceMotion = prefersReducedMotion || isRouteLoading;

  // Update ref when state changes
  useEffect(() => {
    hasShownOfflineToastRef.current = hasShownOfflineToast;
  }, [hasShownOfflineToast]);

  const signalCommandInteraction = useCallback(() => {
    window.dispatchEvent(new CustomEvent('adaptive-command-interaction'));
  }, []);

  const handleThemeUpdate = useCallback(() => {
    const key = resolveThemeKeyFromRoute(location.pathname, location.search);
    setTheme(key);
    clearPreview();
    window.dispatchEvent(new CustomEvent('adaptive-module-change', { detail: { key } }));
  }, [clearPreview, location.pathname, location.search, setTheme]);

  useEffect(() => {
    handleThemeUpdate();
  }, [handleThemeUpdate]);

  // Hide command center when navigating to a different page
  useEffect(() => {
    if (!isInitializedRef.current) {
      isInitializedRef.current = true;
      return;
    }
    // Only hide command when location actually changes
    setCommandVisible(false);
    signalCommandInteraction();
  }, [location.pathname, location.search, signalCommandInteraction]);

  const handleCommandVisibilityChange = useCallback(() => {
    if (isCommandVisible) {
      commandSectionRef.current?.scrollIntoView({ behavior: 'smooth', block: 'start' });
    }
    signalCommandInteraction();
  }, [isCommandVisible, signalCommandInteraction]);

  useEffect(() => {
    handleCommandVisibilityChange();
  }, [isCommandVisible, handleCommandVisibilityChange]);

  useEffect(() => {
    const root = document.documentElement;
    if (!root) return;
    if (shouldReduceMotion) {
      root.classList.add('reduce-motion');
    } else {
      root.classList.remove('reduce-motion');
    }
  }, [shouldReduceMotion]);
  // Listen for offline mode activation and show toast notification
  const handleOfflineActivation = useCallback(() => {
    if (!hasShownOfflineToastRef.current && user) {
      showToast({
        type: 'info',
        title: 'Offline Mode Active',
        message: 'You can continue working offline. Changes will sync automatically when you\'re back online.',
        durationMs: 6000,
      });
      hasShownOfflineToastRef.current = true;
      setHasShownOfflineToast(true);
      signalCommandInteraction();
    }
  }, [user, showToast, signalCommandInteraction]);

  useEffect(() => {
    window.addEventListener('offline-mode-activated', handleOfflineActivation);
    return () => {
      window.removeEventListener('offline-mode-activated', handleOfflineActivation);
    };
  }, [handleOfflineActivation]);

  const handleRouteLoadingStart = useCallback(() => setIsRouteLoading(true), []);
  const handleRouteLoadingStop = useCallback(() => setIsRouteLoading(false), []);

  if (isLoading) {
    return <LogoLoader fullScreen size="xl" showText label="Loading Application" />;
  }

  if (!user) {
    if (authView === 'login') {
      return (
        <LoginForm
          onSuccess={() => refreshUser()}
          onSwitchToRegister={() => setAuthView('register')}
        />
      );
    } else {
      return (
        <RegisterForm
          onSwitchToLogin={() => setAuthView('login')}
        />
      );
    }
  }

  return (
    <div className="relative min-h-screen overflow-hidden text-amber-50" style={{
      backgroundColor: 'var(--bg)',
      transition: shouldReduceMotion ? undefined : 'background-color 100ms linear'
    }}>
      <SkipLinks />

      <div className="pointer-events-none absolute inset-0 overflow-hidden">
        <div
          className="absolute inset-0 transition-opacity duration-500"
          style={{
            background: 'linear-gradient(140deg, var(--nre-gradient-start), var(--nre-gradient-end))',
            opacity: 0.92,
          }}
        />
        <div
          className="absolute -inset-[45%] opacity-90"
          style={{
            background:
              'radial-gradient(circle at 18% 22%, var(--nre-ambient) 0%, transparent 58%), radial-gradient(circle at 78% 12%, var(--nre-ambient-secondary) 0%, transparent 60%)',
            animation: shouldReduceMotion ? 'none' : 'nre-gradient-pan var(--nre-gradient-duration) linear infinite',
            mixBlendMode: 'screen',
          }}
        />
        <div
          className="absolute inset-x-0 bottom-[-28%] h-[55vh] transition-opacity duration-500"
          style={{
            background:
              'radial-gradient(circle at 50% 0%, var(--nre-ambient-secondary) 0%, transparent 70%)',
          }}
        />
        <div className="adaptive-haze" />
        <div className="adaptive-system-overlay" />
      </div>

      <div className="relative z-10 flex min-h-screen flex-col">
        <Header />

        <div className="relative flex flex-1 flex-col">
          {isCommandVisible ? (
            <section
              ref={commandSectionRef}
              className="relative mx-auto flex w-full max-w-6xl flex-1 flex-col justify-center"
            >
              <div className="mb-6 flex justify-end">
                <button
                  type="button"
                  onClick={() => {
                    setCommandVisible(false);
                    signalCommandInteraction();
                  }}
                  className="inline-flex h-12 w-12 items-center justify-center rounded-full bg-black/70 text-white shadow-[0_18px_46px_rgba(0,0,0,0.45)] backdrop-blur-xl transition-transform duration-300 hover:-translate-y-0.5 focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-offset-2 focus-visible:ring-offset-black"
                  style={{
                    backgroundColor: accentSoft,
                    color: '#FFFFFF',
                    boxShadow: `0 18px calc(46px * var(--nre-glow-scale, 1)) ${accentGlow}`,
                  }}
                  aria-label="Close Orbital Command Center"
                >
                  <CloseIcon className="h-5 w-5" />
                </button>
              </div>
              <CommandNavigation onNavigate={() => {
                setCommandVisible(false);
                signalCommandInteraction();
              }} />
            </section>
          ) : (
            <main
              id="main-content"
              role="main"
              className="flex-1 overflow-y-auto"
              style={{
                backgroundColor: 'var(--surface)',
                transition: shouldReduceMotion ? undefined : 'background-color 100ms linear',
              }}
            >
              <Suspense
                fallback={
                  <RouteSuspenseFallback
                    onStart={handleRouteLoadingStart}
                    onStop={handleRouteLoadingStop}
                  />
                }
              >
                <Routes>
                  <Route path="/dashboard" element={<LazyDashboard />} />
                  <Route path="/documents" element={<LazyDocumentsPage />} />
                  <Route path="/crm" element={<LazyCRMPage />} />
                  <Route
                    path="/admin"
                    element={
                      <AdminRoute isAdmin={isAdmin}>
                        <LazyAdminPage />
                      </AdminRoute>
                    }
                  />
                  {/* Redirect root to default based on role */}
                  <Route
                    path="/"
                    element={
                      isAdmin ? (
                        <Navigate to="/admin" replace />
                      ) : isMarketing ? (
                        <Navigate to="/crm" replace />
                      ) : (
                        <Navigate to="/dashboard" replace />
                      )
                    }
                  />
                  {/* Catch all - redirect to home */}
                  <Route path="*" element={<Navigate to="/" replace />} />
                </Routes>
              </Suspense>
            </main>
          )}
        </div>
      </div>

      {!isCommandVisible ? (
        <button
          type="button"
          onClick={() => {
            setCommandVisible(true);
            signalCommandInteraction();
          }}
          className="fixed bottom-6 right-6 z-40 inline-flex h-14 w-14 items-center justify-center rounded-full bg-black/70 text-white shadow-[0_16px_45px_rgba(0,0,0,0.45)] backdrop-blur-xl transition-all duration-300 hover:-translate-y-0.5 hover:shadow-[0_24px_55px_rgba(0,0,0,0.55)] focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-offset-2 focus-visible:ring-offset-black"
          style={{
            backgroundColor: accentSoft,
            color: onAccent,
            boxShadow: `0 20px calc(52px * var(--nre-glow-scale, 1)) ${accentGlow}`,
          }}
          aria-label="Re-open Orbital Command Center"
        >
          <CommandIcon className="h-5 w-5" />
        </button>
      ) : null}
    </div>
  );
}

function App() {
  return (
    <AuthProvider>
      <ThemeSyncProvider>
        <AdaptiveAtmosphereProvider>
          <AppContent />
          <SyncQueueModal />
          {/* Offline indicator and sync error handler shown even before authentication */}
          <OfflineIndicator />
          <SyncErrorHandler />
        </AdaptiveAtmosphereProvider>
      </ThemeSyncProvider>
    </AuthProvider>
  );
}

export default App;

import { useState } from 'react';

interface LogoGeometricProps {
  variant?: 'full' | 'icon' | 'horizontal';
  className?: string;
  isDark?: boolean;
  showTagline?: boolean;
  animate?: boolean;
}

export const LogoGeometric = ({ 
  variant = 'full', 
  className = '', 
  isDark = true,
  showTagline = false,
  animate = false
}: LogoGeometricProps) => {
  const [isHovered, setIsHovered] = useState(false);
  
  const goldColor = '#d4af37';
  const goldLight = '#eab308';
  const textColor = isDark ? '#ffffff' : '#0d1117';

  const IconGeometric = () => (
    <svg
      viewBox="0 0 200 200"
      width={160}
      height={160}
      xmlns="http://www.w3.org/2000/svg"
      className={animate && isHovered ? 'animate-pulse-gold' : ''}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <defs>
        <linearGradient id="netGradient" x1="0%" y1="0%" x2="100%" y2="100%">
          <stop offset="0%" stopColor={goldLight} />
          <stop offset="50%" stopColor={goldColor} />
          <stop offset="100%" stopColor="#b8960f" />
        </linearGradient>
        <filter id="netGlow" x="-50%" y="-50%" width="200%" height="200%">
          <feGaussianBlur stdDeviation="2" result="coloredBlur" />
          <feMerge>
            <feMergeNode in="coloredBlur" />
            <feMergeNode in="SourceGraphic" />
          </feMerge>
        </filter>
      </defs>

      <path
        d="M 100 30 L 145 55 L 145 105 L 100 130 L 55 105 L 55 55 Z"
        fill="none"
        stroke={goldColor}
        strokeWidth="2.5"
        opacity="0.6"
      />

      <path
        d="M 100 50 L 130 70 L 130 110 L 100 130 L 70 110 L 70 70 Z"
        fill="none"
        stroke={goldColor}
        strokeWidth="2"
        opacity="0.8"
      />

      <circle cx="100" cy="30" r="8" fill="url(#netGradient)" filter="url(#netGlow)" />
      <circle cx="145" cy="55" r="8" fill="url(#netGradient)" filter="url(#netGlow)" />
      <circle cx="145" cy="105" r="8" fill="url(#netGradient)" filter="url(#netGlow)" />
      <circle cx="100" cy="130" r="8" fill="url(#netGradient)" filter="url(#netGlow)" />
      <circle cx="55" cy="105" r="8" fill="url(#netGradient)" filter="url(#netGlow)" />
      <circle cx="55" cy="55" r="8" fill="url(#netGradient)" filter="url(#netGlow)" />

      <circle cx="100" cy="80" r="10" fill="url(#netGradient)" filter="url(#netGlow)" />

      <line x1="100" y1="80" x2="100" y2="30" stroke={goldColor} strokeWidth="1.5" opacity="0.4" />
      <line x1="100" y1="80" x2="145" y2="55" stroke={goldColor} strokeWidth="1.5" opacity="0.4" />
      <line x1="100" y1="80" x2="145" y2="105" stroke={goldColor} strokeWidth="1.5" opacity="0.4" />
      <line x1="100" y1="80" x2="100" y2="130" stroke={goldColor} strokeWidth="1.5" opacity="0.4" />
      <line x1="100" y1="80" x2="55" y2="105" stroke={goldColor} strokeWidth="1.5" opacity="0.4" />
      <line x1="100" y1="80" x2="55" y2="55" stroke={goldColor} strokeWidth="1.5" opacity="0.4" />

      <path
        d="M 100 30 L 110 50 L 90 50 Z"
        fill={goldColor}
        opacity="0.5"
      />
      <path
        d="M 145 105 L 135 115 L 145 125 Z"
        fill={goldColor}
        opacity="0.5"
      />

      <ellipse cx="110" cy="70" rx="6" ry="4" fill="white" opacity="0.3" />
    </svg>
  );

  if (variant === 'icon') {
    return (
      <div className={`flex items-center justify-center ${className}`}>
        <IconGeometric />
      </div>
    );
  }

  if (variant === 'horizontal') {
    return (
      <div className={`flex items-center gap-3 ${className}`} onMouseEnter={() => animate && setIsHovered(true)} onMouseLeave={() => animate && setIsHovered(false)}>
        <div className={`flex-shrink-0 ${animate && isHovered ? 'animate-pulse-gold' : ''}`}>
          <svg
            viewBox="0 0 200 200"
            width={48}
            height={48}
            xmlns="http://www.w3.org/2000/svg"
          >
            <defs>
              <linearGradient id="netGradient2" x1="0%" y1="0%" x2="100%" y2="100%">
                <stop offset="0%" stopColor={goldLight} />
                <stop offset="50%" stopColor={goldColor} />
                <stop offset="100%" stopColor="#b8960f" />
              </linearGradient>
              <filter id="netGlow2" x="-50%" y="-50%" width="200%" height="200%">
                <feGaussianBlur stdDeviation="2" result="coloredBlur" />
                <feMerge>
                  <feMergeNode in="coloredBlur" />
                  <feMergeNode in="SourceGraphic" />
                </feMerge>
              </filter>
            </defs>
            <path d="M 100 30 L 145 55 L 145 105 L 100 130 L 55 105 L 55 55 Z" fill="none" stroke={goldColor} strokeWidth="2.5" opacity="0.6" />
            <path d="M 100 50 L 130 70 L 130 110 L 100 130 L 70 110 L 70 70 Z" fill="none" stroke={goldColor} strokeWidth="2" opacity="0.8" />
            <circle cx="100" cy="30" r="8" fill="url(#netGradient2)" filter="url(#netGlow2)" />
            <circle cx="145" cy="55" r="8" fill="url(#netGradient2)" filter="url(#netGlow2)" />
            <circle cx="145" cy="105" r="8" fill="url(#netGradient2)" filter="url(#netGlow2)" />
            <circle cx="100" cy="130" r="8" fill="url(#netGradient2)" filter="url(#netGlow2)" />
            <circle cx="55" cy="105" r="8" fill="url(#netGradient2)" filter="url(#netGlow2)" />
            <circle cx="55" cy="55" r="8" fill="url(#netGradient2)" filter="url(#netGlow2)" />
            <circle cx="100" cy="80" r="10" fill="url(#netGradient2)" filter="url(#netGlow2)" />
          </svg>
        </div>
        <div className="flex flex-col">
          <div className="font-bold text-lg tracking-tight" style={{ color: goldColor }}>
            NRETech
          </div>
          {showTagline && (
            <div className="text-xs tracking-widest uppercase" style={{ color: textColor, opacity: 0.7 }}>
              IT'S TIME TO MAKE IT
            </div>
          )}
        </div>
      </div>
    );
  }

  return (
    <div className={`flex flex-col items-center gap-4 ${className}`} onMouseEnter={() => animate && setIsHovered(true)} onMouseLeave={() => animate && setIsHovered(false)}>
      <IconGeometric />
      <div className="text-center">
        <div className="font-bold text-2xl tracking-tight" style={{ color: goldColor }}>
          NRETech
        </div>
        {showTagline && (
          <div className="text-xs tracking-widest uppercase mt-2" style={{ color: textColor, opacity: 0.7 }}>
            IT'S TIME TO MAKE IT
          </div>
        )}
      </div>
    </div>
  );
};

import { useState } from 'react';

interface LogoMonogramProps {
  variant?: 'full' | 'icon' | 'horizontal';
  className?: string;
  isDark?: boolean;
  showTagline?: boolean;
  animate?: boolean;
}

export const LogoMonogram = ({ 
  variant = 'full', 
  className = '', 
  isDark = true,
  showTagline = false,
  animate = false
}: LogoMonogramProps) => {
  const [isHovered, setIsHovered] = useState(false);
  
  const goldColor = '#d4af37';
  const goldLight = '#eab308';
  const darkAlt = '#1a1f27';
  const textColor = isDark ? '#ffffff' : '#0d1117';

  // Icon: NT Monogram in shield/badge shape
  const IconMonogram = () => (
    <svg
      viewBox="0 0 200 200"
      width={160}
      height={160}
      xmlns="http://www.w3.org/2000/svg"
      className={animate && isHovered ? 'animate-scale-subtle' : ''}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <defs>
        <linearGradient id="monoGradient" x1="0%" y1="0%" x2="100%" y2="100%">
          <stop offset="0%" stopColor={goldLight} />
          <stop offset="50%" stopColor={goldColor} />
          <stop offset="100%" stopColor="#b8960f" />
        </linearGradient>
        <linearGradient id="bgGradient" x1="0%" y1="0%" x2="100%" y2="100%">
          <stop offset="0%" stopColor={darkAlt} />
          <stop offset="100%" stopColor="#0d1117" />
        </linearGradient>
        <filter id="monoGlow" x="-50%" y="-50%" width="200%" height="200%">
          <feGaussianBlur stdDeviation="2.5" result="coloredBlur" />
          <feMerge>
            <feMergeNode in="coloredBlur" />
            <feMergeNode in="SourceGraphic" />
          </feMerge>
        </filter>
      </defs>

      {/* Shield background */}
      <path
        d="M 100 25 L 150 50 L 150 105 Q 100 155 100 155 Q 100 155 50 105 L 50 50 Z"
        fill="url(#bgGradient)"
        stroke={goldColor}
        strokeWidth="2.5"
      />

      {/* Inner shield highlight */}
      <path
        d="M 100 30 L 145 52 L 145 105 Q 100 150 100 150 Q 100 150 55 105 L 55 52 Z"
        fill="none"
        stroke={goldColor}
        strokeWidth="1.5"
        opacity="0.5"
      />

      {/* Monogram text - NT in gold */}
      <text
        x="100"
        y="110"
        fontSize="70"
        fontWeight="900"
        fontFamily="'Plus Jakarta Sans', 'Inter', sans-serif"
        fill="url(#monoGradient)"
        textAnchor="middle"
        dominantBaseline="middle"
        filter="url(#monoGlow)"
        letterSpacing="-3"
      >
        NT
      </text>

      {/* Decorative corner accents */}
      <circle cx="60" cy="35" r="4" fill={goldColor} opacity="0.6" />
      <circle cx="140" cy="35" r="4" fill={goldColor} opacity="0.6" />

      {/* Bottom accent line */}
      <line
        x1="75"
        y1="145"
        x2="125"
        y2="145"
        stroke={goldColor}
        strokeWidth="2"
        opacity="0.7"
      />

      {/* Subtle reflection on shield */}
      <ellipse cx="95" cy="80" rx="12" ry="8" fill="white" opacity="0.15" />
    </svg>
  );

  if (variant === 'icon') {
    return (
      <div className={`flex items-center justify-center ${className}`}>
        <IconMonogram />
      </div>
    );
  }

  if (variant === 'horizontal') {
    return (
      <div className={`flex items-center gap-3 ${className}`} onMouseEnter={() => animate && setIsHovered(true)} onMouseLeave={() => animate && setIsHovered(false)}>
        <div className={`flex-shrink-0 ${animate && isHovered ? 'animate-scale-subtle' : ''}`}>
          <svg
            viewBox="0 0 200 200"
            width={48}
            height={48}
            xmlns="http://www.w3.org/2000/svg"
          >
            <defs>
              <linearGradient id="monoGradient2" x1="0%" y1="0%" x2="100%" y2="100%">
                <stop offset="0%" stopColor={goldLight} />
                <stop offset="50%" stopColor={goldColor} />
                <stop offset="100%" stopColor="#b8960f" />
              </linearGradient>
              <linearGradient id="bgGradient2" x1="0%" y1="0%" x2="100%" y2="100%">
                <stop offset="0%" stopColor={darkAlt} />
                <stop offset="100%" stopColor="#0d1117" />
              </linearGradient>
              <filter id="monoGlow2" x="-50%" y="-50%" width="200%" height="200%">
                <feGaussianBlur stdDeviation="2.5" result="coloredBlur" />
                <feMerge>
                  <feMergeNode in="coloredBlur" />
                  <feMergeNode in="SourceGraphic" />
                </feMerge>
              </filter>
            </defs>
            <path d="M 100 25 L 150 50 L 150 105 Q 100 155 100 155 Q 100 155 50 105 L 50 50 Z" fill="url(#bgGradient2)" stroke={goldColor} strokeWidth="2.5" />
            <path d="M 100 30 L 145 52 L 145 105 Q 100 150 100 150 Q 100 150 55 105 L 55 52 Z" fill="none" stroke={goldColor} strokeWidth="1.5" opacity="0.5" />
            <text x="100" y="110" fontSize="70" fontWeight="900" fontFamily="'Plus Jakarta Sans', 'Inter', sans-serif" fill="url(#monoGradient2)" textAnchor="middle" dominantBaseline="middle" filter="url(#monoGlow2)" letterSpacing="-3">
              NT
            </text>
          </svg>
        </div>
        <div className="flex flex-col">
          <div className="font-bold text-lg tracking-tight" style={{ color: goldColor }}>
            NRETech
          </div>
          {showTagline && (
            <div className="text-xs tracking-widest uppercase" style={{ color: textColor, opacity: 0.7 }}>
              IT'S TIME TO MAKE IT
            </div>
          )}
        </div>
      </div>
    );
  }

  // Full variant (default)
  return (
    <div className={`flex flex-col items-center gap-4 ${className}`} onMouseEnter={() => animate && setIsHovered(true)} onMouseLeave={() => animate && setIsHovered(false)}>
      <IconMonogram />
      <div className="text-center">
        <div className="font-bold text-2xl tracking-tight" style={{ color: goldColor }}>
          NRETech
        </div>
        {showTagline && (
          <div className="text-xs tracking-widest uppercase mt-2" style={{ color: textColor, opacity: 0.7 }}>
            IT'S TIME TO MAKE IT
          </div>
        )}
      </div>
    </div>
  );
};

